const express = require("express");
const bodyParser = require("body-parser");
const morgan = require("morgan");
const passport = require("passport");
const LocalStrategy = require("passport-local").Strategy;
const routes = require("./routes/index");
const bcrypt = require("bcryptjs");
const { User } = require("./db");
const nodemailer = require("nodemailer");


const server = express();

//-----------------------------------------------
//            LOCAL STRATEGY                    |
//-----------------------------------------------
passport.use(
  new LocalStrategy(
    {
      usernameField: "email",
      passwordField: "password",
      passReqToCallback: true,
    },
    (req, email, password, done) => {
      User.findOne({ where: { email } }).then((user) => {
        if (!user) {
          return done(null, false, { message: `User ${email} not found` });
        } else if (!bcrypt.compareSync(password, user.password)) {
          return done(null, false, { message: "Password incorrect" });
        } else return done(null, user.dataValues);
      });
    }
  )
);


//-------------------------------------
//        SERIALIZAR USUARIO          |
//-------------------------------------
passport.serializeUser((user, done) => {
  return done(null, user.id);
});

//-------------------------------------
//        DESERIALIZAR USUARIO        |
//-------------------------------------
passport.deserializeUser((id, done) => {
  User.findByPk(id)
    .then((user) => {
      return done(null, user.get());
    })
    .catch((err) => done(err, false));
});

server.name = "API";
server.use(bodyParser.urlencoded({ extended: true, limit: "50mb" }));
server.use(bodyParser.json({ limit: "50mb" }));
server.use(morgan("dev"));

//-----------------------------------------------
//           INICIALIZAR PASSPORT Y SESSION     |
//-----------------------------------------------
server.use(passport.initialize());
server.use(passport.session());


server.use((req, res, next) => {
  res.header("Access-Control-Allow-Origin", "http://localhost:19006"); // update to match the domain you will make the request from
  res.header("Access-Control-Allow-Credentials", "true");
  res.header(
    "Access-Control-Allow-Headers",
    "Origin, X-Requested-With, Content-Type, Accept"
  );
  res.header("Access-Control-Allow-Methods", "POST, GET, DELETE, PUT, OPTIONS");
  next();
});

server.use("/", routes);

server.use((err, req, res, next) => {
  // eslint-disable-line no-unused-vars
  const status = err.status || 500;
  const message = err.message || err;
  console.error(err);
  res.status(status).send(message);
});

// Error catching endware.
server.use((err, req, res, next) => {
  // eslint-disable-line no-unused-vars
  const status = err.status || 500;
  const message = err.message || err;
  /* console.error(err); */
  res.status(status).send(message);
});

//-----------------------------------------------
//           SEND EMAIL 					              |
//-----------------------------------------------

server.post("/send-email", (req, res) => {
  const email = req.body.email;
  User.findOne({
    where: {
      email: email
    }
  }).then(data => {
    const code = data.generatedCode
    const transporter = nodemailer.createTransport({
      service: "gmail",
      auth: {
        user: "henrybank03@gmail.com",
        pass: "!HenryBank02",
      },
    });

    const mailOptions = {
      from: "Remitente",
      to: email,
      subject: "Envíado desde mi Henry Bank",
      text: `HOLA ${code}`
    };

    transporter.sendMail(mailOptions, function (error, response) {
      if (error) {
        console.log(error);
        res.sendStatus(500);
      } else {
        res.sendStatus(200);
      }
    });
  });
})

module.exports = server;
